using System; using System.Text; using System.Diagnostics;  namespace ByteDance.Foundation {     public static class Utility     {         public static string GetStackTrace(int skips)         {             StackTrace st = new StackTrace(true);             StringBuilder sb = new StringBuilder();             for (int i = skips + 1; i < st.FrameCount; i++)                 sb.Append(st.GetFrame(i).ToString()+"\n");             return sb.ToString();         }     } }